mapper.insert:
insert into report_main
(
	ContentNum
	,Report_Num
    ,StartWork
    ,EndWork
    ,ReportContents
    ,DailyRemarks
)
values

(

	get_seq('seq_report_main')
	,#{content.Report_Num}
	,#{content.StartWork}
	,#{content.EndWork}
	,#{content.ReportContents}
	,#{content.DailyRemarks}

)

insert into report_list
(
	ReportNum
	,User_Num
    ,User_Name
    ,Location
    ,StartWeek
    ,EndWeek
    ,WeeklyRemarks
)
values
(
	get_seq("seq_report_list")
	,#{User_Num}
	,#{User_Name}
	,#{Location}
	,#{StartWeek}
	,#{EndWeek}
	,#{WeeklyRemarks}
)
